#pragma config(Motor,  motor2,          rightRear,     tmotorVexIQ, openLoop, reversed)
#pragma config(Motor,  motor3,          leftRear,      tmotorVexIQ, openLoop)
#pragma config(Motor,  motor4,          rightFore,     tmotorVexIQ, openLoop, reversed)
#pragma config(Motor,  motor5,          leftFore,      tmotorVexIQ, openLoop)
#pragma config(Motor,  motor6,          flipper,       tmotorVexIQ, openLoop)

