task main()
{
	while (true)
	{
		motor[rightRear] = vexRT[Ch2]; //Full power is 127
		motor[rightFore] = vexRT[Ch2]; //Full power is 127

		motor[leftRear] = vexRT[Ch3]; //Full power is 127
		motor[leftFore] = vexRT[Ch3]; //Full power is 127

		//motor[flipper] = vexRT[Ch6]; //Full power is 127
		
		//Arm Control
    if(vexRT[Btn6U] == 1)
    {
      motor[flipper] = 127;
    }
    else if(vexRT[Btn6D] == 1)
    {
      motor[flipper] = -127;
    }
    else
    {
      motor[flipper] = 0;
    }
	}
}
